function PopupLogic() {

 // Detectar si un mensaje est√° oculto para encender el bot√≥n que los muestra
 var MO = 0; // Cantidad Mensajes Ocultos
 var MM = 0; // Cantidad Mensajes Expuestos

 var divP = document.getElementsByTagName("div");

 // Bot√≥n que Muestra Mensajes
 var buttonMM = document.getElementsByClassName("MM")[chat_index];
 if(chat_index >= 0) {
     for(var i = 2; i <= chats[chat_index].children.length-1; i++) {
       if(chats[chat_index].children[i].classList.contains("oculto")) {
         MO++;
       } else {
         MM++;
       }
 }
   if(MO >= 1) { // Si la cantidad de Mensajes Ocultos es mayor que 1
      buttonMM.classList.remove("/*_3QjfB*/");
      buttonMM.classList.add("_3QjfB"); // Poner al bot√≥n color verde
      if(divP["MostrarMensajes()"] != undefined){
        divP["MostrarMensajes()"].style.display = ""; // Mostrar Bot√≥n de "Mostrar Mensajes"
      }
     } else { // Si es 0
       buttonMM.classList.add("/*_3QjfB*/");
       buttonMM.classList.remove("_3QjfB"); // Quitar color verde al bot√≥n
       if(divP["MostrarMensajes()"] != undefined){
        divP["MostrarMensajes()"].style.display = "none"; // Ocultar Bot√≥n
      }
     }

    if(divP["OcultarMensajes()"] != undefined) {
      if(MM >= 1) { // Si la cantidad de Mensajes Mostrados es mayor que 1
       divP["OcultarMensajes()"].style.display = ""; // Mostrar bot√≥n de "Ocultar Mensajes"
     } else {
       divP["OcultarMensajes()"].style.display = "none"; // Ocultar bot√≥n
       }
     }
 }

}

